    const sendSMS = () => {
      const smsURL = 'https://sslsms.cafe24.com/sms_sender.php'; // Cafe24 SMS API URL
      const userId = 'yourUserId'; // Cafe24 계정 아이디
      const secure = 'yourSecureKey'; // Cafe24 계정 인증키
      const sender = '01033356555'; // 발신번호
      const receiver = '01082371502'; // 수신번호
